import { InputsI } from "../../models/Inputs";
import { InputTitleText } from "../../styles/TextStyles";
import { InputBase } from "../Input";

export function setInputs(data: InputsI, register: any, setPopup: any) {
  const Input = (item: any) => {
    switch (item.type) {
      case "popup":
        return (
          <InputBase
            register={(register(item.value), { required: item.required })}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      case "text":
        return (
          <InputBase
            register={(register(item.value), { required: item.required })}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      case "button":
        return (
          <InputBase
            register={(register(item.value), { required: item.required })}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      default:
      case "text":
        return (
          <InputBase
            register={(register(item.value), { required: item.required })}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
    }
  };

  return data.data.map((item, index) => (
    <div
      style={{
        margin: "30px 10px 30px 10px",
      }}
    >
      <InputTitleText>{item.title}</InputTitleText>
      <Input item={item} />
    </div>
  ));
}

export function setSingleInput(data: any, register: any, setPopup: any) {
  return (
    <div
      style={{
        margin: "30px 10px 30px 10px",
      }}
    >
      <InputTitleText>{data.title}</InputTitleText>
      <Input item={data} />
    </div>
  );
}
