import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { LogoImg } from "../../locales/images/logo";
import { getString } from "../../locales/languages/string";
import { Food } from "../../models/Food";
import { FoodTile } from "../../models/FoodComp";
import { clear } from "../../store/cart";
import { increment } from "../../store/data";
import { GridBox } from "../../style";
import { CartBox } from "../../styles/CartBox";
import { FoodData } from "../../testData/FoodData";
import { CartSlider } from "../CartSlider";
import { getWindowSize } from "../functions/getWindowSize";
import { moveCartSlider } from "../functions/moveCartSlider";
import { sortFoodBy } from "../functions/sortFoodBy";
import { popupBlock } from "../PopupBlock";

export const HomeSub = () => {
  const dispatch = useDispatch();
  const [windowSize, setWindowSize] = useState(getWindowSize());
  const [popup, setPopup] = useState({});
  const cart = useSelector((state: any) => state.cart.cart);
  const filters = useSelector((state: any) => state.search);
  const food = useSelector((state: any) => state.food.food);
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  function functionOn() {
    dispatch(clear);
    setPopup({
      isActive: false,
    });
  }

  function getPosition(data: any) {
    const result = food.filter((item: any) => item.id === data)[0];
    const findPosition = food.indexOf(result);
    return findPosition;
  }

  return (
    <>
      {popupBlock({
        popup,
        setPopup,
        functionOn: functionOn,
      })}
      <GridBox
        theme={{
          width: windowSize.innerWidth,
        }}
      >
        {sortFoodBy(food, filters).map((data: any, index: number) =>
          FoodTile(data, getPosition)
        )}
      </GridBox>
      <CartBox
        theme={{
          img: require("../../locales/images/foodBg1.png"),
          width: windowSize.innerWidth,
        }}
      >
        {/* {cart.length !== 0 ? (
          <CartSlider mode={"cart"} cart={cart} setPopup={setPopup} />
        ) : null} */}
      </CartBox>
    </>
  );
};
