import { Food } from "../../models/Food";

export function sortFoodBy(food: Food[], type: any) {
  switch (type) {
    case "amount":
      food.sort((a, b) => a.amount - b.amount);
    case "price":
      food.sort((a, b) => a.price - b.price);
    case "name":
      return food.sort(function ({ a, b }: any) {
        return a.aviable
          ? a.name.localeCompare(b.name)
          : b.name.localeCompare(a.name);
      });
    default:
  }
}
