import styled from "styled-components";
import { colors } from "../locales/colors";

export const ListHandleBox = styled.button`
  border: 1px solid ${colors.buttonbg};
  width: 60%;
  height: ${(props) => (props.theme === true ? null : "40px")};
  border-width: 0px 1px 1px 1px;
  border-radius: 0px 0px 500px 0px;
  margin: 100px;
`;
export const GridMyListBox = styled(GridBox)`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;
