import { useEffect, useState } from "react";
import { colors, rawColors } from "../locales/colors";
import { DeleteImg } from "../locales/images/delete";
import { Food } from "../models/Food";
import { CartFoodElement, FlexPart, SmallImg } from "../style";
import { TestListElementBox } from "../styles/OrderTrans";
import { SerifText, SerifTextS, SerifTextN } from "../styles/TextStyles";
import { FoodPhotoButton } from "./buttons/FoodPhotoButtons";
import { cutText } from "./functions/cutText";

export const CartFood = ({ data, cart, index, number, setCart }: any) => {
  const [amount, setAmount] = useState<number>(data.amount);

  function changeAmountPlus(amount: number) {
    if (number < 99) {
      setAmount(number + amount);
      let newData = [...cart];
      newData[index].amount = number + amount;
      setCart(newData);
    }
  }

  function changeAmountMinus(amount: number) {
    if (number <= 0) {
      setCart(cart.filter((item: any) => item !== data));
    } else {
      setAmount(number - amount);
      let newData = [...cart];
      newData[index].amount = number - amount;
      setCart(newData);
    }
  }

  function deleteFood() {
    setCart(cart.filter((item: any) => item !== data));
  }

  return (
    <TestListElementBox key={data.id.toString()}>
      <FlexPart theme={3}>
        <SerifTextN>{cutText(data.name, 18)}</SerifTextN>
      </FlexPart>
      <FlexPart theme={1} style={{ flexDirection: "column" }}>
        <FlexPart style={{ display: "flex", height: "100%" }}>
          <FlexPart theme={1}>
            <SerifTextN>x{data.amount}</SerifTextN>
          </FlexPart>
          <FlexPart theme={1}>
            <SerifTextN>{data.price * data.amount}$</SerifTextN>
          </FlexPart>
        </FlexPart>
        <FlexPart style={{ justifyContent: "flex-end" }}>
          <FoodPhotoButton
            icon={"delete"}
            sqr={true}
            onClick={() => deleteFood()}
            size={`24px`}
          />
          <FoodPhotoButton
            icon={"minus"}
            sqr={true}
            onClick={() => changeAmountMinus(1)}
            size={`24px`}
          />
          <FoodPhotoButton
            icon={"plus"}
            sqr={true}
            onClick={() => changeAmountPlus(1)}
            size={`24px`}
          />
        </FlexPart>
      </FlexPart>
    </TestListElementBox>
  );
};
