import { useState } from "react";
import { paymentMethods } from "../styles/Constants";
import {
  InputBaseBox,
  InputButtonBox,
  InputChoose,
  OptionBox,
} from "../styles/Input";
import { ErrorInputText } from "../styles/TextStyles";
import { countTime } from "./functions/countTime";
import { useForm } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { useDispatch } from "react-redux";
import { setPopup } from "../store/popup";
export const InputPopup = ({ data, register }: any) => {
  return (
    <InputChoose
      type="submit"
      theme={{ width: "100%" }}
      placeholder={data?.placeholder}
      defaultValue={data?.defaultValue}
      required={data.required}
      pattern={data.pattern}
    >
      {data.value === "deliveryTime"
        ? countTime().map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))
        : paymentMethods.map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))}
    </InputChoose>
  );
};

export const InputBase = ({ data, register, errors }: any) => {
  return (
    <>
      <InputBaseBox
        theme={{ width: "100%" }}
        type={data.typeOfData}
        placeholder={data?.placeholder}
        required={data.required}
        pattern={data.pattern}
      />
    </>
  );
};

export const InputButton = ({
  data,
  register,

  getValues,
}: any) => {
  const dispatch = useDispatch();
  return (
    <InputButtonBox
      type={"button"}
      theme={{ width: "100%" }}
      value={
        getValues()[data.value] ? getValues()[data.value] : data.defaultValue
      }
      onClick={() =>
        dispatch(
          setPopup({
            message: "pickListName",
            type: "choose",
            onConfirm: "AddMyList",
          })
        )
      }
    />
  );
};
