import { useState } from "react";
import { useLocation } from "react-router-dom";
import { Footer } from "../components/Footer";
import { Header } from "../components/Header";
import { MainBody } from "../components/MainBody";
import { HomeSub } from "../components/subScreens/HomeSub";
import { CategoryFIlters, FiltersList } from "../locales/filters";
import { Filter } from "../models/Filter";

export const HomeScr = () => {
  const { state } = useLocation();
  const [filter, setFilter] = useState<Filter>(
    state?.filters ? state?.filters : FiltersList[0]
  );
  const [catFilters, setCatFilters] = useState(
    state?.cat ? state?.cat : CategoryFIlters
  );
  const [numberOnSite, setNumberOnSite] = useState<number>(30);
  const props = {
    filter,
    setFilter,
    catFilters,
    setCatFilters,
    numberOnSite,
    setNumberOnSite,
  };

  return (
    <MainBody props={props}>
      <HomeSub filter={filter} data={state} />
    </MainBody>
  );
};
