import styled from "styled-components";
import { colors, rawColors } from "../locales/colors";

export const InputBase = styled.input`
  color: ${rawColors.white_700};
  display: flex;
  width: ${(props) => (props.theme.width ? props.theme.width : "20px")};
  height: ${(props) => (props.theme.height ? props.theme.height : "35px")};
  border-radius: 10px;
  rows: 2;
  border-width: 1px;
  border: 1px solid ${rawColors.green2_400};
  padding: 0px 0px 0px 5px;
  ::placeholder,
  ::-webkit-input-placeholder {
    color: ${colors.placeholderColor};
  }
  :-ms-input-placeholder {
    color: ${colors.placeholderColor};
  }
`;

export const InputButton = styled(InputBase)`
  display: flex;
  width: ${(props) => (props.theme.width ? props.theme.width : "20px")};
  height: ${(props) => (props.theme.height ? props.theme.height : "70px")};
`;

export const InputSpecialText = styled.input`
  color: ${rawColors.white_700};
  display: flex;
  width: ${(props) => (props.theme.width ? props.theme.width : "20px")};
  height: 80px;
  border-radius: 10px;
  border-width: 1px;
  border: 1px solid ${rawColors.green2_400};
  padding: 0px 0px 0px 5px;
  ::placeholder,
  ::-webkit-input-placeholder {
    color: ${colors.placeholderColor};
  }
  :-ms-input-placeholder {
    color: ${colors.placeholderColor};
  }
`;

export const InputSpecialPopup = styled.input`
  color: ${rawColors.white_700};
  display: flex;
  width: ${(props) => (props.theme.width ? props.theme.width : "20px")};
  height: 80px;
  border-radius: 10px;
  border-width: 1px;
  border: 1px solid ${rawColors.green2_400};
  padding: 0px 0px 0px 5px;
  ::placeholder,
  ::-webkit-input-placeholder {
    color: ${colors.placeholderColor};
  }
  :-ms-input-placeholder {
    color: ${colors.placeholderColor};
  }
`;

export const InputChoose = styled.select`
  color: ${rawColors.white_700};
  display: flex;
  width: ${(props) => (props.theme.width ? props.theme.width : "20px")};
  height: ${(props) => (props.theme.height ? props.theme.height : "75px")};
  border-radius: 10px;
  rows: 2;
  border-width: 1px;
  border: 1px solid ${rawColors.green2_400};
  padding: 0px 0px 0px 5px;
  ::placeholder,
  ::-webkit-input-placeholder {
    color: ${colors.placeholderColor};
  }
  :-ms-input-placeholder {
    color: ${colors.placeholderColor};
  }
`;

export const InputChooseBtn = styled.input`
  display: flex;
  width: ${(props) => (props.theme.width ? props.theme.width : "20px")};
  height: ${(props) => (props.theme.height ? props.theme.height : "75px")};
  border-radius: 10px;
  rows: 2;
  border-width: 1px;
  border: 1px solid ${rawColors.green2_400};
  background-color: white;
  padding: 0px 0px 0px 5px;
`;
