import { useEffect } from "react";
import { colors } from "../locales/colors";
import { pl } from "../locales/languages/pl";
import { Food } from "../models/Food";
import {
  CardSliderBox,
  CartDownBox,
  CartFoodElement,
  CardMainBox,
} from "../style";
import { LoginButton } from "../styles/Buttons";
import { IrishText, OswaldText, SerifText } from "../styles/TextStyles";
import { CartFood } from "./CartFood";

export const CartSlider = ({ cart, setCart }: any) => {
  function priceAll() {
    let x: number = 0;
    cart.map((data: any) => (x = x + data.price * data.amount));
    return Math.round(x * 100) / 100;
  }

  function generateList() {
    let clone = [...cart];
    let count = clone.filter((data) => data.id === clone[clone.length - 1].id);
    let qqq = clone.findIndex(
      (data1) => data1.id === clone[clone.length - 1].id
    );

    if (count.length >= 2) {
      clone[qqq].amount = clone[qqq].amount + clone[cart.length - 1].amount;
      cart.pop();
    }
    return clone.map((element: Food, index: number) => (
      <CartFood
        data={element}
        cart={clone}
        setCart={setCart}
        index={index}
        number={element.amount}
      />
    ));
  }

  return (
    <CardSliderBox id="CardSlider">
      <CartMainBox>{generateList()}</CartMainBox>

      <CartDownBox>
        <SerifText>Suma: {priceAll()}$</SerifText>
        <div
          style={{
            display: "flex",
            justifyContent: "space-evenly",
            width: "100%",
            paddingTop: "10px",
          }}
        >
          <LoginButton theme={colors.filterBtn}>{pl.order}</LoginButton>
          <LoginButton theme={colors.filterBtn} onClick={() => setCart([])}>
            {pl.clear}
          </LoginButton>
        </div>
      </CartDownBox>
    </CardSliderBox>
  );
};
