import { useEffect, useState } from "react";
import { colors, rawColors } from "../locales/colors";
import { DeleteImg } from "../locales/images/delete";
import { Food } from "../models/Food";
import { CartFoodElement, FlexPart, SmallImg } from "../style";
import { AddSubButt } from "../styles/Buttons";
import { FoodPhotoButton } from "./buttons/FoodPhotoButtons";
import { cutText } from "./functions/cutText";

export const CartFood = ({ data, cart, index, number, setCart }: any) => {
  const [amount, setAmount] = useState<number>(data.amount);
  let value = data.price * amount;

  function changeAmountPlus() {
    setAmount(number + 5);
    let newData = [...cart];
    newData[index].amount = number + 1;
    setCart(newData);
  }

  function changeAmountMinus() {
    if (number <= 0) {
      setCart(cart.filter((item: any) => item !== data));
    } else {
      setAmount(number - 5);
      let newData = [...cart];
      newData[index].amount = number - 1;
      setCart(newData);
    }
  }

  return (
    <CartFoodElement key={data.id.toString()}>
      <FlexPart theme={1}>
        <SmallImg alt="No img?" src={data.imageUrl} />
      </FlexPart>
      <FlexPart theme={3}>
        {cutText(data.name, 8) + " "}
        {value.toFixed(2)}$ x{number}
      </FlexPart>
      <FlexPart theme={1}>
        <FoodPhotoButton
          icon={"delete"}
          color={colors.buttonColor1}
          sqr={true}
          onClick={() => (amount > 1 ? setAmount(amount - 1) : null)}
          small
        />
        <FoodPhotoButton
          icon={"minus"}
          color={colors.buttonColor1}
          sqr={true}
          onClick={() => changeAmountMinus()}
          small
        />
        <FoodPhotoButton
          icon={"plus"}
          color={colors.buttonColor1}
          sqr={true}
          onClick={() => changeAmountPlus()}
          small
        />
        {/* <AddSubButt onClick={() => changeAmountMinus()}>-</AddSubButt>

        <AddSubButt onClick={() => changeAmountPlus()}>+</AddSubButt>
        <AddSubButt onClick={() => changeAmountPlus()}>
          <DeleteImg color={rawColors.green} />
        </AddSubButt> */}
      </FlexPart>
    </CartFoodElement>
  );
};
