import { useEffect } from "react";
import { colors, rawColors } from "../locales/colors";
import { pl } from "../locales/languages/pl";
import { Food } from "../models/Food";
import MoonLoader from "react-spinners/ClipLoader";
import { GrayButton } from "../styles/Buttons";
import {
  CardSliderBox,
  CartMainBox,
  CartDownBox,
  CartDownButtonBox,
} from "../styles/CartBox";
import { ButtonText, OswaldText, SerifText } from "../styles/TextStyles";
import { CartFood } from "./CartFood";
import { useNavigate } from "react-router-dom";
import { priceAll } from "./functions/priceAll";
import { generateList } from "./functions/itemList";
import { popupBlock } from "./PopupBlock";

export const CartSlider = ({ cart, setCart, mode }: any) => {
  const navigate = useNavigate();
  const [popup, setPopup] = useState({ isActive: false, variant: "" });

  return (
    <CardSliderBox id="CardSlider">
      <CartMainBox>{generateList(cart, setCart, mode)}</CartMainBox>
      {popupBlock({
        popup,

        setPopup,
      })}
      {
        mode === "cart" ? (
          <CartDownBox>
            <SerifText>Suma: {priceAll(cart)}$</SerifText>
            <CartDownButtonBox>
              <GrayButton
                onClick={() =>
                  navigate("/OrderTransaction", {
                    state: {
                      cart,
                    },
                  })
                }
              >
                <ButtonText>{pl.order}</ButtonText>
              </GrayButton>
              <GrayButton onClick={() => setCart([])}>
                <ButtonText>{pl.clear}</ButtonText>
              </GrayButton>
            </CartDownButtonBox>
            <GrayButton
              onClick={() =>
                navigate("/MyLists", {
                  state: {
                    cart,
                  },
                })
              }
            >
              <ButtonText>{pl.saveList}</ButtonText>
            </GrayButton>
          </CartDownBox>
        ) : null
        // <MoonLoader color={colors.buttonbgAct} />
      }
    </CardSliderBox>
  );
};
