import styled from "styled-components";
import { rawColors, colors } from "../locales/colors";

export const Mainbox = styled.div`
  min-height: 100%;
  margin-top: 50px;
  display: flex;
`;

export const GridBox = styled.div`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

export const FoodBox = styled.div`
  border: 1px solid ${colors.foodBorder};
  display: flex;
  flex-direction: column;
`;

export const FoodPhoto = styled.div`
  height: 220px;
  background-image: url(${(props) => props.theme});
  display: flex;
`;

export const FoodButtonsDiv = styled.div`
  display: flex;
  flex-direciton: row;
  flex: 1;
  justify-content: space-between;
  padding-bottom: 2px;
  align-self: flex-end;
`;

export const FoodBoxTitleCont = styled.section`
  flex: 1;
  display: flex;
  width: 216px;
  align-items: center;
`;

export const FoodBoxRestConst = styled.section`
  flex-direction: row;
  display: flex;
  justify-content: space-around;
  flex: 1;
  align-items: flex-end;
`;

export const FoodBoxAmountCont = styled.section`
  flexdirection: row;
  display: flex;
  height: 50%;
  margin-bottom: 2px;
`;
