import React, { useState } from "react";
import {
  FoodBox,
  FoodBoxText,
  FoodButtonsDiv,
  FoodPhoto,
} from "../styles/MainScreen";
import { AddSubButt } from "../styles/Buttons";
import { Text } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { colors } from "../locales/colors";

export const FoodTile = (data: Food, cart: Food[], setCart: any) => {
  const [amount, setAmount] = useState(1);

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  return (
    <FoodBox key={data.id.toString()}>
      <FoodPhoto theme={data.imageUrl}>
        <FoodButtonsDiv>
          <FoodPhotoButton icon={"cart"} color={colors.buttonColor1} />
          <FoodPhotoButton icon={"info"} color={colors.buttonColor1} />
        </FoodButtonsDiv>
      </FoodPhoto>
      <FoodBoxText>{data.name}+qwe qwe qwe qwe qwe qwe qweqweqwe </FoodBoxText>

      {/* <button onClick={() => add()}>Add to cart</button> */}
      {/* <div style={{ flexDirection: "row" }}>
        <AddSubButt onClick={() => (amount > 0 ? setAmount(amount - 1) : null)}>
          -
        </AddSubButt>
        <Text>{amount}</Text>
        <AddSubButt onClick={() => setAmount(amount + 1)}>+</AddSubButt>
      </div> */}
      {/* <Text>{data.name}</Text> */}
    </FoodBox>
  );
};
