import { useState, useEffect } from "react";
import { Food } from "../../models/Food";
import {
  Constants,
  inputDataDelivery,
  inputDataPersonal,
  inputDataSpecial,
} from "../../styles/Constants";
import {
  FormBox,
  FormSpecialBox,
  GridBox,
  TestListElementBox,
} from "../../styles/OrderTrans";
import {
  InputSectionText,
  InputTitleText,
  SerifText,
} from "../../styles/TextStyles";
import { FoodData } from "../../testData/FoodData";
import { moveCartSlider } from "../functions/moveCartSlider";
import { priceAll } from "../functions/priceAll";
import { SummaryList } from "../SummaryList";
import { useForm } from "react-hook-form";
import { InputsI } from "../../models/Inputs";
import { countTime } from "../functions/countTime";
import { popupBlock } from "../PopupBlock";
import { setInputs } from "../functions/setInputsToForm";
import { GrayButtonInput } from "../../styles/Input";
import { CartBox } from "../../styles/CartBox";
import { CartSlider } from "../CartSlider";
import { Form } from "../Form";

export const OrderTransactionSub = ({ data }: any) => {
  const [cart, setCart] = useState<Food[]>(data ? data : []);
  const [windowSize, setWindowSize] = useState(getWindowSize());
  const [popup, setPopup] = useState({ isActive: false, variant: "" });
  function getWindowSize() {
    const { innerWidth, innerHeight } = window;
    return { innerWidth, innerHeight };
  }
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart?.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  const {
    register,
    setValue,
    handleSubmit,
    getValues,
    formState: { errors },
  } = useForm<FormData>();
  const onSubmit = (data: any) => console.log(getValues);

  return (
    <>
      <GridBox onSubmit={handleSubmit(onSubmit)}>
        <InputSectionText>{Constants.forms.deliveryData}</InputSectionText>
        <FormBox>
          {setInputs(inputDataDelivery, register, setPopup, errors)}
        </FormBox>
        <InputSectionText>{Constants.forms.deliveryTime}</InputSectionText>
        <FormBox>
          {setInputs(inputDataPersonal, register, setPopup, errors)}
        </FormBox>
        <FormSpecialBox>
          {setInputs(inputDataSpecial, register, setPopup, getValues, errors)}
        </FormSpecialBox>
        <GrayButtonInput type="submit" />
      </GridBox>
      <CartBox
        theme={{
          img: require("../../locales/images/foodBg1.png"),
          width: windowSize.innerWidth,
        }}
      >
        <CartSlider cart={cart} setCart={setCart} mode={"order"} />
      </CartBox>
    </>
  );
};
