import { ButtonPhoto } from "../../style";
import { chooseIcon } from "../functions/getIcon";

export const FoodPhotoButton = ({
  icon,
  onClick,
  sqr,
  isActive,
  setInfoVisible,
}: any) => {
  return (
    <ButtonPhoto
      theme={{ sqr, isActive }}
      onClick={isActive === false ? null : onClick}
      onMouseEnter={() => (icon === "icon" ? setInfoVisible(true) : null)}
      onMouseLeave={() => (icon === "icon" ? setInfoVisible(false) : null)}
    >
      {chooseIcon(icon, isActive)}
    </ButtonPhoto>
  );
};
