import { Food } from "../../models/Food";

export function sortFoodByAlph(food: Food[], type: boolean) {
  return food.sort(function (a, b) {
    if (type) return a.name.localeCompare(b.name);
    else return b.name.localeCompare(a.name);
  });
}

export function sortFoodByOther(food: Food[], type: any) {
  const x: string = type.toString();
  return food[0][x];
}
