import React, { useRef, useState } from "react";
import {
  FoodBox,
  FoodBoxAmountCont,
  FoodBoxRestConst,
  FoodBoxTitleCont,
  FoodButtonsDiv,
  FoodPhoto,
} from "../styles/MainScreen";
import { AddSubButt } from "../styles/Buttons";
import { FoodBoxText, Text, TextOswald, TextSerif } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { colors } from "../locales/colors";
import { cutText } from "../components/functions/cutText";
import { BlockImg } from "../locales/images/block";

export const FoodTile = (data: Food, cart: Food[], setCart: any) => {
  const [amount, setAmount] = useState(1);
  const [infoVisible, setInfoVisible] = useState<boolean>(false);

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  return (
    <FoodBox key={data.id.toString()}>
      <FoodPhoto theme={data.imageUrl}>
        {infoVisible ? null : (
          <>
            <TextOswald>Not available</TextOswald>
            <div style={{ position: "absolute" }}>
              <BlockImg />
            </div>
          </>
        )}
        <FoodButtonsDiv>
          <FoodPhotoButton icon={"info"} isActive={data.aviable} />
          <FoodPhotoButton
            icon={"cart"}
            onClick={() => add()}
            isActive={data.aviable}
            setInfoVisible={setInfoVisible}
          />
        </FoodButtonsDiv>
      </FoodPhoto>
      <FoodBoxTitleCont>
        <FoodBoxText>{cutText(data.name, 45)}</FoodBoxText>
      </FoodBoxTitleCont>
      <FoodBoxRestConst>
        <TextSerif>{data.price}$</TextSerif>
        {data.aviable ? (
          <FoodBoxAmountCont>
            <FoodPhotoButton
              icon={"minus"}
              color={colors.buttonColor1}
              sqr={true}
              onClick={() => (amount > 1 ? setAmount(amount - 1) : null)}
            />
            <TextSerif>{amount}</TextSerif>
            <FoodPhotoButton
              icon={"plus"}
              color={colors.buttonColor1}
              sqr={true}
              onClick={() => (amount < 99 ? setAmount(amount + 1) : null)}
            />
          </FoodBoxAmountCont>
        ) : null}
      </FoodBoxRestConst>
    </FoodBox>
  );
};
