import React, { useState } from "react";
import { FoodBox, FoodPhoto } from "../styles/MainScreen";
import { AddSubButt } from "../styles/Buttons";
import { Text } from "../styles/TextStyles";
import { Food } from "./Food";

export const FoodTile = (data: Food, cart: Food[], setCart: any) => {
  const [amount, setAmount] = useState(1);

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  return (
    <FoodBox key={data.id.toString()}>
      <FoodPhoto alt="No img?" src={data.imageUrl} />
      {/* <button onClick={() => add()}>Add to cart</button> */}
