import { flexbox } from "@mui/system";
import { inputDataSpecial } from "../styles/Constants";
import { FormSpecialBox } from "../styles/OrderTrans";
import { setInputs } from "./functions/setInputsToForm";

export function popupBlock(
  options: any,
  windowSize: any,
  handle: any,
  register: any
) {
  if (options.isActive === true) {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "hidden";
    }
    return (
      <div
        style={{
          position: "absolute",
          top: 0,
          width: windowSize.innerWidth,
          height: windowSize.innerWidth,
          backgroundColor: "black",
          opacity: 0.3,
          zIndex: 1000,
        }}
      >
        <div
          style={{
            backgroundColor: "red",
            width: 200,
            height: 200,
            position: "absolute",
          }}
        >
          asdasd
        </div>
        {/* <form onSubmit={handle}>
          <FormSpecialBox>
            {setInputs(inputDataSpecial, register, "")}
          </FormSpecialBox>
        </form> */}
      </div>
    );
  } else {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "auto";
    }
  }
}
