import { useState } from "react";
import { InputBase } from "../styles/Input";
import { ErrorInputText } from "../styles/TextStyles";

export const Input = ({ props, filter, placeholder, type }: any) => {
  const [value, setValue] = useState("");
  let error = "";

  function checkInput(e: any) {
    if (e.match(filter) != null) {
      setValue(e);
    }
  }

  function getMessage(type: string) {
    switch (type) {
      case "email":
        return "SIKE ZŁY EMAIL";
      case "login":
        return "SIKE ZŁY LOGIN";
      case "password":
        return "SIKE ZŁE HASŁO";
      case "noValidation":
        return "SIKE NIE TAKIE HASŁO I LOGIN";
      default:
        return "cokolwiek";
    }
  }

  return (
    <div>
      <InputBase
        {...props}
        value={value}
        onChange={(e) => checkInput(e.target.value)}
        placeholder={placeholder}
        theme={{ width: "200px" }}
      ></InputBase>
      <ErrorInputText>{error ? getMessage(type) : null}</ErrorInputText>
    </div>
  );
};
