import { useState } from "react";
import { colors, rawColors } from "../locales/colors";
import { FiltersList } from "../locales/filters";
import { pl } from "../locales/languages/pl";
import { Button } from "../styles/Buttons";
import { Constants } from "../styles/Constants";
import { SearchFiltersBox } from "../styles/Header";
import { SerifTextS } from "../styles/TextStyles";

export const Filters = ({ filter, setFilter }: any) => {
  function chooseFilter(data: any) {
    setShowList(false);
    setFilter(data);
  }

  const [showList, setShowList] = useState(false);
  return (
    <SearchFiltersBox>
      <div style={{ width: "100px" }}>
        <Button onClick={() => setShowList(!showList)}>
          <SerifTextS style={{ userSelect: "none" }}>
            {!showList ? pl.filters.show : pl.filters.hide}
          </SerifTextS>
        </Button>
        {!showList ? (
          <></>
        ) : (
          FiltersList.map((data) => (
            <Button
              theme={
                filter
                  ? data.id === filter.id
                    ? rawColors.green1_300
                    : rawColors.white_100
                  : rawColors.white_100
              }
              onClick={() => chooseFilter(data)}
            >
              <SerifTextS style={{ userSelect: "none" }}>
                {data.name}
              </SerifTextS>
            </Button>
          ))
        )}
      </div>
    </SearchFiltersBox>
  );
};
