import styled from "styled-components";
import { colors, rawColors } from "../locales/colors";
import { Constants } from "./Constants";

export const Text = styled.label`
  font-style: normal;
  font-variant: small-caps;
  user-select: none;
  text-align: center;
  overflow: hidden;
  font-style: normal;
  line-height: 18px;
`;

export const FoodBoxText = styled(Text)`
  width: 100%;
  font-family: "PT Serif Caption";
  font-size: ${Constants.font.m};
  font-variant: small-caps;
  font-weight: 100;
`;

export const SerifText = styled(Text)`
  font-family: "PT Serif Caption";
  font-size: 150%;
  font-variant: small-caps;
  font-weight: 100;
  line-height: 24px;
  padding: 2px;
`;

export const SerifTextS = styled(Text)`
  font-family: "PT Serif Caption";
  font-size: ${Constants.font.s};
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100px;
  width: 100%;
`;

export const SerifTextSS = styled(Text)`
  font-family: "PT Serif Capion";
  font-size: ${Constants.font.xs};
  font-variant: small-caps;
  font-weight: 100px;
`;

export const OswaldText = styled(Text)`
  font-family: Oswald;
  font-size: ${Constants.font.xxl}
  font-variant: normal;
  font-weight: 700;
  line-height: 30px;
  color: red;
  text-shadow: -1px 0 black, 0 1pxblack, 1px 0 black, 0 -1px black;
  position: absolute;
  margin-top: 70px;
  width: ${Constants.size.foodPhotoSize};
`;

export const IrishText = styled(Text)`
  font-family: "Irish Grover";
  font-size: ${Constants.font.xxl};
  font-variant: small-caps;
  line-height: 24px;
  padding: 2px;
`;

export const LinkTextBox = styled(SerifTextS)`
  display: flex;
  width: 90px;
  height: 20px;
  flex-direction: row;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const ButtonText = styled(IrishText)`
  font-size: ${Constants.font.l};
`;

export const ErrorInputText = styled(Text)`
  font-size: ${Constants.font.xs};
  color: ${colors.errorColor};
  width: ${(props) => (props.theme ? props.theme : "160px")};
  text-align: left;
  line-height: 30px;
`;
