import { useEffect } from "react";
import { colors, rawColors } from "../locales/colors";
import { pl } from "../locales/languages/pl";
import { Food } from "../models/Food";
import MoonLoader from "react-spinners/ClipLoader";
import { GrayButton } from "../styles/Buttons";
import {
  CardSliderBox,
  CartMainBox,
  CartDownBox,
  CartDownButtonBox,
} from "../styles/CartBox";
import { ButtonText, OswaldText, SerifText } from "../styles/TextStyles";
import { CartFood } from "./CartFood";
import { useNavigate } from "react-router-dom";
import { priceAll } from "./functions/priceAll";
import { generateList } from "./functions/itemList";
import { getString } from "../locales/languages/string";

export const CartSlider = ({ cart, setCart, mode, setPopup }: any) => {
  const navigate = useNavigate();

  return (
    <CardSliderBox id="CardSlider">
      <CartMainBox>{generateList(cart, setCart, mode)}</CartMainBox>

      {
        mode === "cart" ? (
          <CartDownBox>
            <SerifText>
              {getString("other", "amount")} {priceAll(cart)}$
            </SerifText>
            <CartDownButtonBox>
              <GrayButton
                onClick={() =>
                  navigate("/OrderTransaction", {
                    state: {
                      cart,
                    },
                  })
                }
              >
                <ButtonText>{getString("buttons", "order")}</ButtonText>
              </GrayButton>
              <GrayButton
                onClick={() =>
                  setPopup({
                    isActive: true,
                    message: getString("message", "areYouSureClear"),
                    type: "yikes",
                  })
                }
              >
                <ButtonText>{getString("buttons", "clear")}</ButtonText>
              </GrayButton>
            </CartDownButtonBox>
            <GrayButton
              onClick={() =>
                navigate("/MyLists", {
                  state: {
                    cart,
                  },
                })
              }
            >
              <ButtonText
                onClick={() =>
                  setPopup({
                    isActive: true,
                    message: getString("message", "pickListName"),
                    type: "yikes",
                  })
                }
              >
                {getString("buttons", "saveList")}
              </ButtonText>
            </GrayButton>
          </CartDownBox>
        ) : null
        // <MoonLoader color={colors.buttonbgAct} />
      }
    </CardSliderBox>
  );
};
