import { flexbox } from "@mui/system";
import { inputDataSpecial } from "../styles/Constants";
import { FormSpecialBox } from "../styles/OrderTrans";
import { setInputs, setInputs2 } from "./functions/setInputsToForm";

export function popupBlock(options: any, handle: any, register: any) {
  if (options.isActive === true) {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "hidden";
    }
    return (
      <div
        style={{
          position: "fixed",
          top: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0,0,0,0.3)",
          zIndex: 1000,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div
          style={{
            backgroundColor: "red",
            width: "50%",
            height: "50%",
            borderRadius: "50px",
          }}
        >
          <form onSubmit={handle}>
            <div>{setInputs2(inputDataSpecial.data[0], register, "")}</div>
          </form>
        </div>
      </div>
    );
  } else {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "auto";
    }
  }
}
