import { useState } from "react";
import { useForm } from "react-hook-form";
import {
  Constants,
  inputDataDelivery,
  inputDataPersonal,
  inputDataSpecial,
  loginData,
  newListData,
} from "../../styles/Constants";
import { setInputs, setSingleInput } from "../functions/setInputsToForm";
import { Flex, GridBox2 } from "../../style";
import { FormBox, FormSpecialBox } from "../../styles/OrderTrans";
import { GrayButtonInput, GrayButtonLogin } from "../../styles/Input";
import {
  ButtonText,
  InputSectionText,
  SerifText,
} from "../../styles/TextStyles";
import { GrayButton, GrayButtonPopup } from "../../styles/Buttons";
import { LoginBox } from "../../styles/Header";
import { getString } from "../../locales/languages/string";

export const FormInput = (props: any) => {
  const {
    register,
    setValue,
    handleSubmit,
    getValues,
    formState: { errors },
  } = useForm<FormData>();
  const onSubmit = (data: any) => console.log(getValues());
  const [popup, setPopup] = useState({});

  function accept() {
    props?.setPopup({ isActive: false, type: "" });
    handleSubmit(onSubmit);
  }

  return (
    <div style={{ width: "90%", marginLeft: "5%" }}>
      <>{setSingleInput(newListData.data[0], register, setPopup)}</>
      <Flex>
        <GrayButtonPopup
          onClick={() => props?.setPopup({ isActive: false, type: "" })}
        >
          {getString("buttons", "goBack")}
        </GrayButtonPopup>
        <GrayButtonPopup onClick={() => accept()}>
          {getString("buttons", "save")}
        </GrayButtonPopup>
      </Flex>
    </div>
  );
};
