import { flexbox } from "@mui/system";
import { GrayButton, GrayButtonPopup } from "../styles/Buttons";
import { inputDataSpecial, inputDataSpecialPopup } from "../styles/Constants";
import {
  PopupWindowMainBox,
  PopupWindowBox,
  GrayButtonInput,
} from "../styles/Input";
import { FormSpecialBox } from "../styles/OrderTrans";
import { setSingleInput, setInputs } from "./functions/setInputsToForm";
import { PopupChoose } from "./popups/PopupChoose";
import { PopupConfirm } from "./popups/PopupConfirm";

export function popupBlock(props: any) {
  function selectPopup() {
    switch (props?.popup.type) {
      case "choose":
        return <PopupChoose {...props} />;
      case "confirm":
        return <PopupConfirm {...props} />;
      default:
        return <PopupConfirm {...props} />;
    }
  }

  if (props?.popup.isActive === true) {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflowX = "scroll";
      // html.style.position = "fixed";
    }
    return (
      <PopupWindowMainBox>
        <>{selectPopup()}</>
      </PopupWindowMainBox>
    );
  } else {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflowX = "auto";
      html.style.position = "static";
    }
  }
}
