export function showProductInfo(data: any) {
  let e = document.getElementById(`InfoBox${data.id}`);
  let xx = document.getElementById(`Button${data.id}`);
  console.log(xx.matches(":hover"));
  if (e && xx && xx.matches(":hover") === true) {
    e.style.opacity = `1`;
    e.style.transition = "0.6s ease-out";
  } else if (e) {
    e.style.opacity = `0`;
    e.style.transition = "0.6s ease-out";
  }
}
