import styled from "styled-components";
import { rawColors, colors } from "./locales/colors";

export const FooterBox = styled.div`
  background-color: ${rawColors.pink};
  height: 50px;
  position: relative;
  position: relative;
`;

export const MainBodyBox = styled.div`
  background-color: ${colors.bg};
  min-height: 100%;
  margin-top: 50px;
  display: flex;
`;

export const FoodBox = styled.div`
  border: 2px solid red;
  border-radius: 30px;
  margin: 10px;
  display: flex;
  flex-direction: column;
`;

export const GridBox = styled.div`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

export const FoodPhoto = styled.img``;

export const CartFoodElement = styled.div`
  height: 50px;
  width: 95%;
  display: flex;
  background: linear-gradient(90deg, rgba(107, 172, 0, 0.8) 0%, #f6f3f3 100%);
  margin: 10px 0px 0px 10px;
`;

export const SmallImg = styled.img`
  width: 44px;
  height: 44px;
  align-self: center;
  justify-content: center;
  display: flex;
`;

export const FlexPart = styled.div`
  display: flex;
  flex: ${(props) => props.theme};
  align-items: center;
  justify-content: flex-end;
`;

export const BgLogo = styled.div`
  position: fixed;
  width: 80%;
  height: 80%;
  top: 15%;
  justify-content: center;
  display: flex;
  align-items: center;
  z-index: 1;
  filter: blur(10px);
  opacity: 0.3;
`;

export const Flex = styled.div`
  display: flex;
`;
