import styled from "styled-components";
import { rawColors, colors } from "../locales/colors";

export const FormBox = styled.div`
  flex: 1;
  display: grid;
  grid-template-columns: 30% 30%;
  margin: 0px 0px 0px ${(props) => (props.theme.width <= 800 ? `20%` : "0%")};
  justify-content: center;
`;

export const GridBox = styled.div`
  width: ${(props) => (props.theme.width <= 800 ? `100%` : "70%")};
  // display: flex;
`;
export const SummaryBox = styled.div`
  position: fixed;
  right: 0;
  height: 90%;
  width: ${(props) => (props.theme.width <= 800 ? `0%` : "20%")};
  min-width: ${(props) => (props.theme.width <= 800 ? `0%` : "200px")};
  overflow: hidden;
  z-index: 0;
  background-image: url(${(props) => props.theme.img});
  background-repeat: no-repeat;
  background-size: 100% 100%;
  background-color: red;
`;
