import styled from "styled-components";

export const AddSubButt = styled.button`
  background-color: rgba(52, 52, 52, 0);
  //   border-radius: 100px;
  border-width: 0px;
  color: blue;
  font-size: 25px;
  &:hover {
    transform: translate(${(props) => props.theme}, 0);
    transition: 1s ease-out;
  }
`;
