import { Food } from "../../models/Food";

export function sortFoodBy(food: Food[], filters: any) {
  switch (type) {
    case "amount":
      return food.sort((a, b) => a.amount - b.amount);
    case "price":
      return food.sort((a, b) => a.price - b.price);
    case "name":
      return food.sort(function (a, b) {
        if (a.name < b.name) {
          return -1;
        }
        if (a.name > b.name) {
          return 1;
        }
        return 0;
      });
    default:
      return food;
  }
}
