import styled from "styled-components";
import { colors, rawColors } from "../locales/colors";

export const HeaderBox = styled.div`
  background-color: ${colors.buttonbgUnact};
  height: 50px;
  position: fixed;
  width: 100%;
  top: 0;
  display: flex;
  justify-content: center;
  z-index: 10;
  box-shadow: 0 2px 20px 0;
`;

export const TitleTxt = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 2;
  background-color: ${colors.bg};
`;

export const LoginBox = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 2;
  background-color: ${colors.bg};
`;

export const SearchMain = styled.div`
  z-index: 1;
  background-color: ${colors.bg};
  min-width: 220px;
  height: 60px;
  top: -10px;
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
  &:hover {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
    box-shadow: 0 4px 8px 0;
  }
  &:not(:hover) {
    position: fixed;
    transform: translate(0%, 0%);
    transition: 0.3s ease-out;
  }
`;

export const CircleBox = styled.div`
  background-color: ${colors.bg};
  border-radius: 0 0 100px 100px;
  position: absolute;
  width: 50px;
  height: 50px;
  margin: 60px;
  box-shadow: 0 4px 8px 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const SearchHiddenHandle = styled.div`
  background-color: red;
  width: 50px;
  height: ${(props) => (props.theme === "xD" ? `0px` : `2100px`)};
  position: absolute;
  opacity: 0.5;
`;

export const SerachInput = styled.input`
  color: ${rawColors.red};
  width: 90%;
  display: flex;
`;

export const SearchBarBox = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SearchFiltersBox = styled.div`
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;
  z-index: 999;
  background-color: red;
`;

export const LogoBox = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  z-index: 2;
  background-color: ${colors.bg};
`;

export const LogoSubBox = styled.div`
  margin-left: 25%;

  &:hover {
    cursor: pointer;
  }
  &:active {
    filter: brightness(1.1);
  }
`;
