import { useEffect, useState } from "react";
import { Food } from "../models/Food";
import { CartFoodElement, FlexPart, SmallImg } from "../style";

export const CartFood = ({ data, cart, index, setCart, number }: any) => {
  const [amount, setAmount] = useState<number>(data.amount);

  function changeAmountPlus() {
    setAmount(amount + 1);
    let newData = [...cart];
    newData[index].amount = amount + 1;
    setCart(newData);
  }

  function changeAmountMinus() {
    if (amount <= 0) {
      setCart(cart.filter((item: any) => item !== data));
    } else {
      setAmount(amount - 1);
      let newData = [...cart];
      newData[index].amount = amount - 1;
      setCart(newData);
    }
  }

  return (
    <CartFoodElement>
      <FlexPart theme={1}>
        <SmallImg alt="No img?" src={data.imageUrl} />
      </FlexPart>
      <FlexPart theme={3}>
        {data.name}
        {data.price * amount}$
      </FlexPart>
      <FlexPart theme={1}>
        <button onClick={() => changeAmountMinus()}>-</button>
        {amount}
        <button onClick={() => changeAmountPlus()}>+</button>
      </FlexPart>
      {/* <div style={{ flex: 1 }}>
        {data.name} {data.amount} {data.price}$
      </div>
      <div style={{ flex: 1 }}>
        <SmallImg alt="No img?" src={data.imageUrl} />
      </div> */}
    </CartFoodElement>
  );
};
