import { paymentMethods } from "../styles/Constants";
import {
  InputBaseBox,
  InputButtonBox,
  InputChoose,
  OptionBox,
} from "../styles/Input";
import { countTime } from "./functions/countTime";
import { useDispatch } from "react-redux";
import { setPopup } from "../store/popup";
export const InputPopup = ({ props }: any) => {
  return (
    <InputChoose
      type="submit"
      theme={{ width: "100%" }}
      placeholder={props?.data?.placeholder}
      defaultValue={props?.data?.defaultValue}
      required={props?.data.required}
      pattern={props?.data.pattern}
      {...props?.register(props?.data.value)}
    >
      {data.value === "deliveryTime"
        ? countTime().map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))
        : paymentMethods.map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))}
    </InputChoose>
  );
};

export const InputBase = ({ props }: any) => {
  return (
    <>
      <InputBaseBox
        theme={{ width: "100%" }}
        type={props?.data.typeOfData}
        placeholder={props?.data?.placeholder}
        required={props?.data.required}
        pattern={props?.data.pattern}
        {...props?.register(data.value)}
      />
    </>
  );
};

export const InputButton = ({ data, register, valueTemp, getValues }: any) => {
  const dispatch = useDispatch();
  return (
    <InputButtonBox
      type={"button"}
      theme={{ width: "100%" }}
      value={
        getValues()[data.value] ? getValues()[data.value] : data.defaultValue
      }
      onClick={
        () =>
          dispatch(
            setPopup({ isActive: true, variant: data.value, type: "choose" })
          )
        // setOpenPopup({ isActive: true, variant: data.value, type: "choose" })
      }
    />
  );
};
