import { useEffect, useState } from "react";
import {
  FoodBox,
  FoodBoxAmountCont,
  FoodBoxRestConst,
  FoodBoxTitleCont,
  FoodButtonsDiv,
  FoodPhoto,
} from "../styles/MainScreen";
import { FoodBoxText, OswaldText, SerifText } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { colors } from "../locales/colors";
import { cutText } from "../components/functions/cutText";
import { BlockImg } from "../locales/images/block";
import { showProductInfo } from "../components/functions/showProductInfo";
import { InfoScreen } from "../components/InfoScreen";
import { Constants } from "../styles/Constants";
import { pl } from "../locales/languages/pl";

export const FoodTile = (data: Food, setCart: any) => {
  const [amount, setAmount] = useState(1);
  const [infoVisible, setInfoVisible] = useState<boolean>(false);

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  useEffect(() => {
    showProductInfo(infoVisible, data);
  }, [infoVisible]);

  return (
    <FoodBox key={data.id.toString()}>
      <InfoScreen id={data.id}>
        <SerifText>{data.desc}</SerifText>
      </InfoScreen>
      <FoodPhoto theme={data.imageUrl}>
        {data.aviable ? null : (
          <>
            <OswaldText>{pl.home.notAvailable}</OswaldText>
            <div style={{ position: "absolute" }}>
              <BlockImg />
            </div>
          </>
        )}
        <FoodButtonsDiv>
          <FoodPhotoButton
            icon={"info"}
            isActive={data.aviable}
            setInfo={setInfoVisible}
            size={`30px`}
          />
          <FoodPhotoButton
            icon={"cart"}
            onClick={() => add()}
            isActive={data.aviable}
            size={`30px`}
          />
        </FoodButtonsDiv>
      </FoodPhoto>
      <FoodBoxTitleCont>
        <FoodBoxText>{cutText(data.name, 32)}</FoodBoxText>
      </FoodBoxTitleCont>
      <FoodBoxRestConst>
        <SerifText>
          {data.price}$/{data.unit}
        </SerifText>
        {data.aviable ? (
          <FoodBoxAmountCont>
            <FoodPhotoButton
              icon={"minus"}
              sqr={true}
              size={`25px`}
              onClick={() => (amount > 1 ? setAmount(amount - 1) : null)}
            />
            <SerifText>{amount}</SerifText>
            <FoodPhotoButton
              icon={"plus"}
              sqr={true}
              size={`25px`}
              onClick={() => (amount < 99 ? setAmount(amount + 1) : null)}
            />
          </FoodBoxAmountCont>
        ) : null}
      </FoodBoxRestConst>
    </FoodBox>
  );
};
