import styled from "styled-components";
import { rawColors, colors } from "../locales/colors";
import { Constants } from "./Constants";

export const SearchMainBox = styled.div`
  z-index: 1;
  background-color: ${colors.bg};
  min-width: 400px;
  height: 80px;
  top: -30px;
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
  ${(props) =>
    props.theme === true
      ? `  {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
    box-shadow: 0 4px 8px 0;
  }`
      : `  {
        position: fixed;
        transform: translate(0%, 0%);
        transition: 0.3s ease-out;
      }`};
`;

export const SearchHandleBox = styled.div`
  background-color: ${colors.bg};
  border-radius: 0 0 100px 100px;
  position: absolute;
  width: 50px;
  height: 30px;
  margin: 80px;
  box-shadow: 0 4px 8px 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const SearchSubMainBox = styled.div`
  display: flex;
  width: 100%;
`;

export const SearchInput = styled.input`
  color: ${rawColors.red};
  display: flex;
  margin: 5px 15px 0px 5px;
  width: 260px;

  height: 20px;
`;

export const SearchFiltersBox = styled.div`
  z-index: 999;
  position: absolute;
  right: 0;
  margin: 0px 10px 0px 10px;
`;

export const SearchButtonBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 10%;
`;

export const SearchCategoryBox = styled.div`
  flex-wrap: wrap;
  width: 350px;
  display: flex;
  align-items: center;
  justify-content: center;
`;
