import { useState } from "react";
import { paymentMethods } from "../styles/Constants";
import {
  InputBaseBox,
  InputButtonBox,
  InputChoose,
  InputChooseBtn,
  OptionBox,
} from "../styles/Input";
import { ErrorInputText } from "../styles/TextStyles";
import { countTime } from "./functions/countTime";

export const InputPopup = ({
  data,
  register,
  valueTemp,
  setOpenPopup,
}: any) => {
  return (
    <InputChoose
      type="submit"
      theme={{ width: "100%" }}
      placeholder={data?.placeholder}
      defaultValue={data?.defaultValue}
      {...register}
    >
      {data.value === "deliveryTime"
        ? countTime().map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))
        : paymentMethods.map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))}
    </InputChoose>
  );
};

export const InputBase = ({ data, register }: any) => {
  return (
    <InputBaseBox
      theme={{ width: "100%", height: "80%" }}
      placeholder={data?.placeholder}
      {...register}
    />
  );
};

export const InputButton = ({
  data,
  register,
  valueTemp,
  setOpenPopup,
}: any) => {
  return (
    <InputButtonBox
      type={"button"}
      theme={{ width: "100%" }}
      value={data.defaultValue}
      onClick={() => setOpenPopup({ isActive: true, variant: data.value })}
    />
  );
};
