import { useEffect, useState } from "react";
import { colors, rawColors } from "../locales/colors";
import { DeleteImg } from "../locales/images/delete";
import { Food } from "../models/Food";
import { FlexPart } from "../style";
import { TestListElementBox } from "../styles/OrderTrans";
import { SerifText, SerifTextS, SerifTextN } from "../styles/TextStyles";
import { FoodPhotoButton } from "./buttons/FoodPhotoButtons";
import { cutText } from "./functions/cutText";

export const CartFood = ({ data, cart, index, number, setCart, mode }: any) => {
  function changeAmountPlus(amount: number) {
    if (number < 99) {
      let newData = [...cart];
      newData[index].amount = number + amount;
      setCart(newData);
    }
  }

  function changeAmountMinus(amount: number) {
    if (number <= 0) {
      setCart(cart.filter((item: any) => item !== data));
    } else {
      let newData = [...cart];
      newData[index].amount = number - amount;
      setCart(newData);
    }
  }

  function deleteFood() {
    setCart(cart.filter((item: any) => item !== data));
  }

  return (
    <TestListElementBox key={data.id.toString()} theme={mode}>
      <FlexPart theme={3}>
        <SerifTextN>{cutText(data.name, 18)}</SerifTextN>
      </FlexPart>
      <FlexPart
        theme={2}
        style={{ flexDirection: mode === "cart" ? "column" : "row" }}
      >
        <FlexPart theme={2}>
          <FlexPart theme={1} style={{ justifyContent: "flex-start" }}>
            <SerifTextN>x{data.amount}</SerifTextN>
          </FlexPart>
          <FlexPart theme={1}>
            <SerifTextN>
              {Math.round(data.price * data.amount * 100) / 100}$
            </SerifTextN>
          </FlexPart>
        </FlexPart>
        {mode === "cart" ? (
          <FlexPart theme={1} style={{ justifyContent: "flex-end" }}>
            <FoodPhotoButton
              icon={"delete"}
              sqr={true}
              onClick={() => deleteFood()}
              size={`24px`}
            />
            <FoodPhotoButton
              icon={"minus"}
              sqr={true}
              onClick={() => changeAmountMinus(1)}
              size={`24px`}
            />
            <FoodPhotoButton
              icon={"plus"}
              sqr={true}
              onClick={() => changeAmountPlus(1)}
              size={`24px`}
            />
          </FlexPart>
        ) : null}
      </FlexPart>
    </TestListElementBox>
  );
};
