import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { apiFetchProducts } from "../api";
import { MainBody } from "../components/MainBody";
import { HomeSub } from "../components/subScreens/HomeSub";

export const HomeScr = () => {
  const [products, setProducts] = useState([]);
  const [errors, setErrors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    /* refactor with React Query */
    apiFetchProducts()
      .then((result) => {
        if (result.products !== null) {
          setProducts(
            result.products.sort((a: any, b: any): number =>
              a.name < b.name ? -1 : a.name > b.name ? 1 : 0
            ) ?? []
          );
        }
        setTimeout(() => {
          setIsLoading(false);
          setSnackbarOpen(true);
        }, 500 + Math.random() * 1000);
      })
      .catch((e) => setErrors(e));
  }, []);
  return (
    <MainBody>
      <button onClick={() => console.log(products)} />
      <HomeSub />
    </MainBody>
  );
};
