import { useState } from "react";
import { SearchFiltersBox } from "../style";

export const Filters = () => {
  let list = [
    { name: "Name A-Z" },
    { name: "Name Z-A" },
    { name: "Price from low to high" },
    { name: "Price from high to low" },
    { name: "Amount from low do high" },
    { name: "Amount from high to low" },
  ];

  const [showList, setShowList] = useState(false);
  return (
    <SearchFiltersBox>
      <div style={{ width: "100px" }}>
        <button
          onClick={() => setShowList(!showList)}
          style={{ width: "100px" }}
        >
          {!showList ? "Rozwiń" : "Showaj"}
        </button>
        {!showList ? <></> : list.map((x) => <button>{x.name}</button>)}
      </div>
    </SearchFiltersBox>
  );
};
