import { FilterCategoryButton } from "../../styles/Search";

export const filterCatButton = (data: any, setAllData: any, allData: any) => {
  function check() {
    if (data.choosen) {
      if (data.blocked) {
        let newData = [...allData];
        newData[data.id].blocked = false;
        setAllData(newData);
      } else {
        let newData = [...allData];
        newData[data.id].choosen = false;
        setAllData(newData);
      }
    } else {
      let newData = [...allData];
      newData[data.id].choosen = true;
      setAllData(newData);
    }
    return 0;
  }

  return (
    <FilterCategoryButton
      onClick={() => check()}
      style={{
        backgroundColor: data.choosen
          ? data.blocked
            ? "red"
            : "green"
          : undefined,
      }}
    >
      {data.name}
    </FilterCategoryButton>
  );
};
