import styled from "styled-components";
import { rawColors } from "../locales/colors";

export const CircleBox = styled.div`
  // border-radius: 100%;
  // position: absolute;
  // width: 50px;
  // height: 50px;
  // background-color: ${rawColors.green1_100};
  // margin: 20px 0 0 40%;
  // z-index: 1;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  border-radius: 150px 150px 0 0;
  background-color: green;
`;

export const SerachInput = styled.input`
  color: ${rawColors.red};
`;

export const SearchBarBox = styled.div`
  flex: 2;
  justify-content: center;
  align-items: center;
  display: flex;
`;

export const SearchFiltersBox = styled.div`
  flex: 1;
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;
`;

export const SearchMain = styled.div`
  z-index: 2;
  background-color: ${rawColors.blue};
  max-width: 35%;
  min-width: 400px;
  height: 50px;
  position: absolute;
  display: flex;
  &:hover {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
  }
  &:not(:hover) {
    position: fixed;
    transform: translate(0%, 0%);
    transition: 0.3s ease-out;
  }
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
`;
