import { useState } from "react";
import { SearchFiltersBox } from "../style";

export const Filters = ({ filter }) => {
  let list = [
    { name: "Name A-Z", type: "name", inOrder: true },
    { name: "Name Z-A", type: "name", inOrder: false },
    { name: "Price from low to high", type: "price", inOrder: true },
    { name: "Price from high to low", type: "price", inOrder: false },
    { name: "Amount from low do high", type: "amount", inOrder: true },
    { name: "Amount from high to low", type: "amount", inOrder: false },
  ];

  function chooseFilter(data: any) {
    setShowList(false);
    filter({ type: data.type, inOrder: data.inOrder });
  }

  const [showList, setShowList] = useState(false);
  return (
    <SearchFiltersBox>
      <div style={{ width: "100px" }}>
        <button
          onClick={() => setShowList(!showList)}
          style={{ width: "100px" }}
        >
          {!showList ? "Rozwiń" : "Showaj"}
        </button>
        {!showList ? (
          <></>
        ) : (
          list.map((data) => (
            <button
              style={{ width: "100px" }}
              onClick={() => chooseFilter(data)}
            >
              {data.name}
            </button>
          ))
        )}
      </div>
    </SearchFiltersBox>
  );
};
