import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { LoginMenuButton } from "../../styles/Buttons";
import { SerifText } from "../../styles/TextStyles";
import { chooseIcon } from "../functions/getIcon";

export const MenuButton = ({ label, img, nav, onClick }: any) => {
  const navigate = useNavigate();
  const [active, setActive] = useState<boolean>(false);
  return (
    <>
      <LoginMenuButton onClick={() => (nav ? navigate(`/${nav}`) : onClick())}>
        <div
          style={{
            flex: 1,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {chooseIcon(img, "")}
        </div>
        <div
          style={{
            flex: 6,
            display: "flex",
            alignItems: "center",
            marginLeft: "10px",
          }}
        >
          <SerifText>{label}</SerifText>
        </div>
      </LoginMenuButton>
      {img === "languages" && active ? (
        <>
          <MenuButton img={"xD"} label={"x"} onClick={() => console.log("x")} />
          <MenuButton img={"xD"} label={"x"} onClick={() => console.log("x")} />
        </>
      ) : null}
    </>
  );
};
