import { useState, useEffect } from "react";
import { LogoImg } from "../../locales/images/logo";
import { Food } from "../../models/Food";
import { FoodTile } from "../../models/FoodComp";
import { CartBox } from "../../styles/CartBox";
import {
  Constants,
  inputDataDelivery,
  inputDataPersonal,
  InputsI,
} from "../../styles/Constants";
import { FormBox, GridBox, SummaryBox } from "../../styles/OrderTrans";
import {
  InputTitleText,
  IrishText,
  SectionInpitText,
  SerifText,
  SerifTextS,
} from "../../styles/TextStyles";
import { FoodData } from "../../testData/FoodData";
import { CartSlider } from "../CartSlider";
import { moveCartSlider } from "../functions/moveCartSlider";
import { sortFoodBy } from "../functions/sortFoodBy";
import { Input } from "../Input";

export const OrderTransactionSub = ({ filter }: any) => {
  const [food, setFood] = useState<Food[]>(FoodData);
  const [cart, setCart] = useState<Food[]>([]);
  const [windowSize, setWindowSize] = useState(getWindowSize());

  function getWindowSize() {
    const { innerWidth, innerHeight } = window;
    return { innerWidth, innerHeight };
  }
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  function setInputs(data: InputsI[]) {
    return data.map((item, index) => (
      <div
        style={{
          margin: "30px 10px 30px 10px",
        }}
      >
        <InputTitleText>{item.title}</InputTitleText>
        <Input placeholder={item.placeholder} data={item} />
      </div>
    ));
  }

  return (
    <>
      <GridBox theme={{ width: windowSize.innerWidth }}>
        <SectionInpitText>{Constants.forms.deliveryData}</SectionInpitText>
        <FormBox>{setInputs(inputDataDelivery)}</FormBox>
        <SectionInpitText>{Constants.forms.deliveryData}</SectionInpitText>
        <FormBox>{setInputs(inputDataPersonal)}</FormBox>
      </GridBox>
      <SummaryBox
        theme={{
          width: windowSize.innerWidth,
        }}
      ></SummaryBox>
    </>
  );
};
