import styled from "styled-components";
import { rawColors, colors } from "../locales/colors";

export const FormBox = styled.div`
  flex: 1;
  display: grid;
  grid-template-columns: 45% 45%;
  margin: 0px 0px 0px ${(props) => (props.theme.width <= 800 ? `20%` : "0%")};
  justify-content: center;
`;

export const GridBox = styled.div`
  width: ${(props) => (props.theme.width <= 800 ? `100%` : "70%")};
  padding: 2% 0px 0px 0px;
  // display: flex;
`;
export const SummaryBox = styled.div`
  min-height: 100%;
  width: ${(props) => (props.theme.width <= 800 ? `0%` : "30%")};
  min-width: ${(props) => (props.theme.width <= 800 ? `0%` : "200px")};
  display: flex;
  position: fixed;
  right: 0;
  flex-direction: column;
  opacity: ${(props) => (props.theme.width <= 800 ? `0` : "1")};
`;

export const SectionFormTitleBox = styled.div`
  margin: 0px 0px 0px 20%;
  // display: flex;
`;

export const TestListElementBox = styled.div`
  height: 40px;
  width: 90%;
  border: 1px solid ${rawColors.green1_200};
  border-radius: 100px 0px 0px 100px;
  border-width: 1px 1px 0 1px;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;

export const ListBox = styled.div`
  height: 40px;
  width: 90%;
  border: 1px solid ${rawColors.green1_200};
  border-radius: 100px 0px 0px 100px;
  border-width: 1px 1px 0 1px;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;
