import { createSlice } from "@reduxjs/toolkit";
import { FoodData } from "../testData/FoodData";

export interface FiltersI {
  food: any;
}

const initialState: FiltersI = {
  food: FoodData,
};
export const foodSlice = createSlice({
  name: "food",
  initialState,
  reducers: {
    incrementAmount: (state, action: any) => {
      state.food[action.id].amount += 1;
    },
    decrementAmount: (state, action: any) => {
      state.food[action.id].amount -= 1;
    },
  },
});

export const { incrementAmount, decrementAmount } = foodSlice.actions;

export default foodSlice.reducer;
