import styled from "styled-components";
import { rawColors } from "../locales/colors";

export const CircleBox = styled.div`
  border-radius: 0 0 100px 100px;
  position: absolute;
  width: 100px;
  height: 50px;
  margin: 50px 0 0 40%;
  z-index: 1;
  box-shadow: 0 4px 10px 0;
`;

export const SerachInput = styled.input`
  color: ${rawColors.red};
`;

export const SearchBarBox = styled.div`
  flex: 2;
  justify-content: center;
  align-items: center;
  display: flex;
`;

export const SearchFiltersBox = styled.div`
  flex: 1;
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;
`;

export const SearchMain = styled.div`
  z-index: 2;
  max-width: 35%;
  min-width: 400px;
  height: 50px;
  position: absolute;
  display: flex;

  &:hover {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
    box-shadow: 0 4px 8px 0;
  }
  &:not(:hover) {
    position: fixed;
    transform: translate(0%, 0%);
    transition: 0.3s ease-out;
  }
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
`;
