import { Food } from "../../models/Food";

export function sortFoodBy({ food }: any[], type: string, inOrder: boolean) {
  switch (type) {
    case "amount":
      return food.sort(function (a: any, b: any) {
        return inOrder ? b.amount + a.amount : b.amount - a.amount;
      });
    case "price":
      return food.sort(function (a: any, b: any) {
        return inOrder ? b.price + a.price : b.price - a.price;
      });
    case "name":
      return food.sort(function (a: any, b: any) {
        return inOrder
          ? a.name.localeCompare(b.name)
          : b.name.localeCompare(a.name);
      });
    default:
      return food;
  }
}
