import { ButtonPhoto } from "../../style";
import { chooseIcon } from "../functions/getIcon";

export const FoodPhotoButton = ({
  icon,
  onClick,
  sqr,
  isActive,
  setInfo,
  small,
}: any) => {
  return (
    <ButtonPhoto
      theme={{ sqr, isActive, small }}
      onClick={isActive === false ? null : onClick}
      onMouseEnter={() => (icon === "info" ? setInfo(true) : null)}
      onMouseLeave={() => (icon === "info" ? setInfo(false) : null)}
    >
      {chooseIcon(icon, isActive)}
    </ButtonPhoto>
  );
};
